import { Grid } from "react-loader-spinner";
import { PrimaryColor } from "../constants/constants.js";
import { Loader as ChatLoader } from "@chatscope/chat-ui-kit-react";
export function Loader() {
  return (
    <div
      style={{
        position: "absolute",
        top: "50%",
        left: "50%",
        transform: "translate(-50%, -50%)",
      }}
    >
      <ChatLoader color={PrimaryColor} style={{ height: 300, width: 800, color: PrimaryColor }}></ChatLoader>
      {/* <Grid ariaLabel="loading-indicator" color={PrimaryColor} /> */}
    </div>
  );
}
